<?php/** * Validate an array of string using the given validator */ class Ui_Validator_Iterator extends Ui_Validator{		public $validator;		public function isValid( $values ){		foreach( $values as $value ){			if( $this->validator->isValid( $value ) === false ) return false;		}		return true;	}		public function getMessages(){		return $this->validator->getMessages();	}		public function getPlainMessages(){		return $this->validator->getPlainMessages();	}}?>